mapper.select:
SELECT u.*, u.id uid, u.name uname, r.id rid, r.name rname
FROM sys_user u, sys_role r, sys_role_user sru
WHERE username=#{username} and sru.role_id = r.id and sru.user_id = u.id;

SELECT u.*, u.id uid, u.name uname, r.id rid, r.name rname
FROM sys_user u, sys_role r, sys_role_user sru
WHERE u.id=#{id} and sru.role_id = r.id and sru.user_id = u.id;

SELECT u.*, u.id uid, u.name uname, r.id rid, r.name rname
FROM sys_user u, sys_role r, sys_role_user sru
WHERE sru.role_id = r.id and sru.user_id = u.id;

mapper.update:
UPDATE sys_user u
SET  u.name = #{name} AND u.department = #{department} AND u.department = #{department} AND u.password = #{password}
WHERE u.id = #{id}
